mapper.select:
select r.RECORD_ID as STOCKIN_ID, r.RECORD_SUPPLIERID as STOCKIN_SUPPLIER_ID, s.SUPPLIER_NAME as STOCKIN_SUPPLIER_NAME, r.RECORD_GOODID as STOCKIN_GOOD_ID, g.GOOD_NAME as STOCKIN_GOOD_NAME, r.RECORD_NUMBER as STOCKIN_NUMBER, r.RECORD_TIME as STOCKIN_TIME, r.RECORD_PERSON as STOCKIN_PERSON, r.RECORD_REPOSITORYID as STOCKIN_REPOSITORY_ID from wms_record_in r,wms_goods g,wms_supplier s where r.RECORD_SUPPLIERID = s.SUPPLIER_ID and r.RECORD_GOODID = g.GOOD_ID
and r.RECORD_REPOSITORYID = #{repositoryID}
and r.RECORD_TIME >= #{startDate}
and r.RECORD_TIME <= #{endDate}
