mapper.select:
select r.RECORD_ID as STOCKOUT_ID, r.RECORD_CUSTOMERID as STOCKOUT_CUSTOMER_ID, c.CUSTOMER_NAME as STOCKOUT_CUSTOMER_NAME, r.RECORD_GOODID as STOCKOUT_GOOD_ID, g.GOOD_NAME as STOCKOUT_GOOD_NAME, r.RECORD_NUMBER as STOCKOUT_NUMBER, r.RECORD_TIME as STOCKOUT_TIME, r.RECORD_PERSON as STOCKOUT_PERSON, r.RECORD_REPOSITORYID as STOCKOUT_REPOSITORY_ID from wms_record_out r,wms_goods g,wms_customer c where r.RECORD_CUSTOMERID = c.CUSTOMER_ID and r.RECORD_GOODID = g.GOOD_ID
and r.RECORD_REPOSITORYID = #{repositoryID}
and r.RECORD_TIME >= #{startDate}
and r.RECORD_TIME <= #{endDate}
